pragma solidity ^0.4.25;
    contract Certificados {
        address private owner;
        constructor() public{
        owner=msg.sender;
        } 
        struct Certificado{
            string url;
        }
        mapping(string => Certificado) private certificado;
        function join(string url) public isOwner {
            Certificado storage certificad= certificado[url];
            certificad.url=url;
        }
        function getURL(string url)public view returns(string){
            Certificado memory certificad=certificado[url];
            return (certificad.url);
        }
        modifier isOwner() {
        require(owner==msg.sender);
        _;
        }
    }
