 pragma solidity ^0.4.25;

contract SimpleStorage {
    address private owner;
    constructor() public{
    owner=msg.sender;
    } 
    struct User{
        string url;
    }
    mapping(string => User) private users;
    mapping (string=>bool) private joinedUsers;

    function join(string curp, string url) public isOwner {
        User storage user= users[curp];
        user.url=url;
        joinedUsers[curp]=true;
    }
    function getURL(string curp)public view returns(string){
        require(userJoined(curp));
        User memory user=users[curp];
        return (user.url);
    }

    function userJoined(string curp) private view returns(bool){
        return joinedUsers[curp];
    } 

    modifier isOwner() {
    require(owner==msg.sender);
    _;
    }
}
